class EmailTypeChecker
  def self.edu? email
    if email.blank?
      false
    else
      school_prefix = email.split('@')[1]
      edu_splits = school_prefix.split('.')
      return edu_splits[edu_splits.length - 1] == 'edu'
    end
  end

  def self.corp? email
    if valid?(email) && !edu?(email)
      email_parts = email.split('@')
      company_prefix = email_parts[1]
      (INVALID_EMAIL_PREFIXES.include? company_prefix)? false : true
    else
      false
    end
  end

  def self.valid? email
    email_regex = %r{
      ^ # Start of string
      [0-9a-z] # First character
      [0-9a-z.+_]+ # Middle characters
      [0-9a-z] # Last character
      @ # Separating @ character
      [0-9a-z] # Domain name begin
      [0-9a-z.-]+ # Domain name middle
      [0-9a-z] # Domain name end
      $ # End of string
    }xi

    !!(email =~ email_regex)
  end

  def self.invalid? email
    !valid? email
  end

